import React, { Component } from 'react'
import Square from './components/Square'
import Message from './components/Message'
import './App.css'

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      squares: [...Array(9).fill("")],
      turns: 0
    }
  }

  restartGame = () => {
    let { squares, turns } = this.state
    turns = 0
    this.setState({ turns: turns })
    squares = [...Array(9).fill("")]
    this.setState({ squares: squares })
  }

  handleLocation = (indexLocation) => {
    let { squares, turns } = this.state
    if (turns === 9) {
      alert("Game Over")
    }
    else if (squares[indexLocation] === "x" || squares[indexLocation] === "o") {
      alert("Already Selected")
    }
    else {
      if (turns % 2 === 0) {
        squares[indexLocation] = "x"
      }
      else {
        squares[indexLocation] = "o"
      }
      this.setState({ squares: squares })
      turns += 1
      this.setState({ turns: turns })
      console.log(squares)
    }
  }


  checkWinner = () => {
    let { squares } = this.state

    const winningCombo =
      [
        [0, 1, 2],
        [3, 4, 5],
        [6, 7, 8],
        [0, 3, 6],
        [1, 4, 7],
        [2, 5, 8],
        [0, 4, 8],
        [2, 4, 6]
      ]
    for (let i = 0; i < winningCombo.length; i++) {
      const [a, b, c] = winningCombo[i]
      //console.log(a, b, c)
      //console.log(squares)
      let winner = squares[a]
      //console.log(winner)
      if ((squares[a] === 'x' && squares[b] === 'x' && squares[c] === 'x') ||
        (squares[a] === 'o' && squares[b] === 'o' && squares[c] === 'o')) {
        // if(squares[a] === squares[a] && squares[a] === squares[b] && squares[a] === squares[c]){
        console.log("were here")
        return winner
      }
    }
  }

  gameResult = (winner) => {
    let { squares, turns } = this.state
    //console.log(turns)
    if (winner === "x" || winner === "o") {
      setTimeout(function () {
        alert(winner.toUpperCase() + ' is the winner!!!');
      }, 500);

      //this.restartGame()
      this.setState({ turns: 9 })
    }
    else if (turns === squares.length - 1) {
      setTimeout(function () {
        alert('Cats Game!!!');
      }, 500);
    }

  }


  render() {
    let { squares } = this.state
    let square = squares.map((square, index) => {
      return (
        <Square
          handleLocation={this.handleLocation}
          xoMark={square}
          index={index}
          checkWinner={this.checkWinner}
          gameResult={this.gameResult}
        />
      )
    })
    return (
      <React.Fragment>
        <h1>Tic Tac Toe</h1>
        <div id="gameBoard">
          {square}
        </div>
        <br></br>
        <Message
          gameResult={this.gameResult}
        />
        <button onClick={this.restartGame}>Restart Game</button>
      </React.Fragment>
    )
  }
}
export default App
